import React from 'react'

import './loginForm.css'
class LogForm extends React.Component{

    //设置
    dataChange=(names)=>{
        return(e)=>{
            this.setState({[names]:e.target.value})
        }
    }

    render(){
        console.log(this.props);
        return(
            <div className='form1'>
                <form onSubmit={this.sub}>
                        <span>月份</span><input ></input><br />
                        <span>日期</span><input></input><br />
                        <span>任务</span><input ></input><br />
                        <span>时间</span><input ></input><br />
                        <button onClick={this.props.subMit}>添加</button>
                    </form>
                    <div className='search'>
                        <input type='text' onChange={this.searchT}></input><span onClick={this.search}>搜索</span>
                    </div>
            </div>
        )
    }
}

export default LogForm
