import React from 'react'

import LogForm from './components/logForm/loginForm'
import Logs from './components/logs/logs'
class Demo extends React.Component{

    constructor(){
        super();
        this.state={data:data};
        this.subMit = this.subMit.bind(this)
    }

    subMit(month, day, thing, duration){
        return (e)=>{
            e.preventDefault();
            let newObj={month,day,thing,duration}
            this.setState({data:[newObj,...this.state.data]})
        }
    }

    //删除
    delete = (index) =>{
        let {data} = this.state;
        data.splice(index,1);
        this.setState({data:data});
    }
    
     //查找
     search=()=>{
        let {searchText} = this.state;
        let reg = new RegExp(searchText,'i');
        let newArr=[];
        this.state.redata.forEach(ele=>{
            if(reg.test(ele.thing)){
                newArr.push(ele);
            }
        })
        this.setState({data:newArr});
    }


    render(){
        return(
            <div className='app'>
                <LogForm  subMit={this.subMit} data={this.state.data}/>
                <Logs data={this.state.data} del={this.delete}/>
            </div>
        )
    }
}

const data = [
    { month: '六月', day: 20, thing: '学习JavaScript', duration: '2小时' },
    { month: '六月', day: 22, thing: '学习Nodejs', duration: '1小时' },
    { month: '六月', day: 23, thing: '学习React', duration: '2小时' }
]

export default Demo
